const incoming = require('./dist/index.js').incoming;

const myWebhookUri = 'https://hook.bearychat.com/=bw52O/incoming/f07f3af926932a29d41294b530bccec0';

const message = incoming
  .withText('Hello, world')  // or .withMarkdown('**Hello, world**')
  .to('机器人市场')
  .withAttachment({ text: 'It is simple,' })
  .withAttachment({ text: 'but colorful.', color: '#ffa500' })
  .withAttachment({ text: 'sent with bearychat.js' })
  .build();

incoming.send(myWebhookUri, message)
  .then(() => {
    console.log('incoming sent');
  });
